mapper.update:
UPDATE USERS
SET user_pw = #{user_pw}, user_name = #{user_name}, user_birth = #{user_birth}, user_nickname = #{user_nickname}, user_phone = #{user_phone}, user_addr = #{user_addr}
WHERE user_id = #{user_id}

mapper.select:
SELECT order_num, order_date, order_status
FROM ORDERS
WHERE user_id = #{user_id}

SELECT order_num, book_id, book_cnt
FROM ORDERLIST
WHERE order_num = #{order_num}

SELECT book_id, book_title, book_cover, book_writer, book_price
FROM BOOK
WHERE book_id = #{book_id};

SELECT user_point
FROM USERS
WHERE user_id = #{user_id};
