mapper.select:
		SELECT B.book_id, B.book_cover, B.book_title, B.book_writer, A.book_cnt, B.book_price FROM cart A, book B
  	WHERE user_id = #{user_id}
  	AND A.book_id = B.book_id

		SELECT B.book_id, B.book_cover, B.book_title, B.book_writer, A.book_cnt, B.book_price FROM cart A, book B
  	WHERE user_id = #{user_id}
  	AND A.book_id = B.book_id
  	AND A.book_id = ${book_id}

mapper.update:
UPDATE cart
SET book_cnt = #{book_cnt}
WHERE user_id = #{user_id}
AND book_id = #{book_id}
