mapper.select:
SELECT COUNT(*)
FROM RECORD
WHERE user_id = #{user_id}

mapper.update:
UPDATE RECORD
SET record_init_date = #{record_init_date},
	record_fin_date = #{record_fin_date},
	record_score = #{record_score},
	record_contents = #{record_contents}
WHERE record_id = #{record_id}

mapper.select:
SELECT *
FROM RECORD
WHERE user_id = #{user_id}
AND book_id = #{book_id}
